/* Animation classes are from http://www.justinaguilar.com/animations/ */

.slideRight .badge{
	animation-name: slideRightAndScaleDown;
	-webkit-animation-name: slideRightAndScaleDown;	

	animation-duration: 1.5s;	
	-webkit-animation-duration: 1.5s;

	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;		

	visibility: visible !important;	
}

.slideRight .icons{
	animation-name: slideRightAndRotateCounterClockwise;
	-webkit-animation-name: slideRightAndRotateCounterClockwise;	

	animation-duration: 2s;	
	-webkit-animation-duration: 2s;
/*
	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;		*/

	visibility: visible !important;	
}

@keyframes slideRight {
	0% {
		transform: translateX(-5000%);
	}
	50%{
		transform: translateX(8%);
	}
	65%{
		transform: translateX(-4%);
	}
	80%{
		transform: translateX(4%);
	}
	95%{
		transform: translateX(-2%);
	}			
	100% {
		transform: translateX(0%);
	}	
}

@-webkit-keyframes slideRight {
	0% {
		-webkit-transform: translateX(-80%);
	}
	50%{
		-webkit-transform: translateX(8%);
	}
	65%{
		-webkit-transform: translateX(-4%);
	}
	80%{
		-webkit-transform: translateX(4%);
	}
	95%{
		-webkit-transform: translateX(-2%);
	}			
	100% {
		-webkit-transform: translateX(0%);
	}
}

@keyframes slideRightAndScaleDown {
	0% {
		transform: scale(5.56) translate(calc(-60%), calc(41%));
	}
	100% {
		transform: scale(1) translateX(0%);
	}	
}

@-webkit-keyframes slideRightAndScaleDown {
	0% {
		transform: scale(5.56) translate(-66%, 41%);
	}			
	100% {
		transform: scale(1) translateX(0%);
	}	
}


@keyframes slideRightAndRotateCounterClockwise {
	0% {
		transform: rotate(90deg) translate(calc(800%), calc(75%));
	}			
	100% {
		transform: rotate(0) translateX(0%);
	}	
}


@-webkit-keyframes slideRightAndRotateCounterClockwise {
	0% {
		transform: rotate(90) translate(-66%, 41%);
	}			
	100% {
		transform: rotate(0) translateX(0%);
	}	
}

.slideLeft .badge {
	animation-name: slideLeftAndScaleUp;
	-webkit-animation-name: slideLeftAndScaleUp;	

	animation-duration: 0.5s;	
	-webkit-animation-duration: 0.5s;

	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;		

	visibility: visible !important;
}

.slideLeft .icons {
	animation-name: slideLeftAndRotateClockwise;
	-webkit-animation-name: slideLeftAndRotateClockwise;	

	animation-duration: 0.8s;	
	-webkit-animation-duration: 0.8s;

	animation-timing-function: ease-in-out;	
	-webkit-animation-timing-function: ease-in-out;		

	visibility: visible !important;
}

@keyframes slideLeft {
	0% {
		transform: translateX(80%);
	}
	50%{
		transform: translateX(-8%);
	}
	65%{
		transform: translateX(4%);
	}
	80%{
		transform: translateX(-4%);
	}
	95%{
		transform: translateX(2%);
	}			
	100% {
		transform: translateX(0%);
	}
}

@-webkit-keyframes slideLeft {
	0% {
		-webkit-transform: translateX(50%);
	}
	50%{
		-webkit-transform: translateX(-8%);
	}
	65%{
		-webkit-transform: translateX(4%);
	}
	80%{
		-webkit-transform: translateX(-4%);
	}
	95%{
		-webkit-transform: translateX(2%);
	}			
	100% {
		-webkit-transform: translateX(0%);
	}
}

@keyframes slideLeftAndRotateClockwise {
	0% {
		transform: rotate(-90deg) translate(48%, 450%);
	}
	100% {
		transform: rotate(0deg) translateX(0%);
	}
}

@-webkit-keyframes slideLeftAndRotateClockwise {
	0% {
		transform: rotate(-90deg) translate(48%, 450%);
	}
	100% {
		transform: rotate(0deg) translateX(0%);
	}
}

@keyframes slideLeftAndScaleUp {
	0% {
		transform: scale(0.18) translate(600%, -220%);
	}
	100% {
		transform: scale(1) translateX(0%);
	}
}

@-webkit-keyframes slideLeftAndScaleUp {
	0% {
		transform: scale(0.18) translate(600%, -220%);
	}
	100% {
		transform: scale(1) translateX(0%);
	}
}

