function removeUnwantedSelectors(selector) {
    var after = selector.replace('html > body > ', '');

    return after;
}

function isEditable(selector) {
    if(selector.indexOf('medium-editor') != -1) {
        return false;
    } 

    if(selector.indexOf('not-editable') != -1) {
        return false;
    } 

    return true;
}

function makeEditable(selector) {
    selector = removeUnwantedSelectors(selector);
    if(isEditable(selector) && editors[selector] == null) {
        let obj = document.querySelector(selector);
        if(obj) {
            console.log("Making " + selector + " editable");
            editors[selector] = (new MediumEditor(obj));
        }
    }

    return selector;
}

function wireUpRightClick() {
    document.addEventListener( "contextmenu", function(e) {
        e.preventDefault();
        var el = e.path[0],
            selector = csspath(el),
            success = false;

        success = makeEditable(selector) == selector;
  });
}

window.addEventListener('load',function(){
    window.page.directive = window.page.directive || {};

    Object.keys(window.page.directive).forEach(function(selector){
        try {
            var success = makeEditable(selector) == selector;
        }
        catch(e) {
            console.log(e);
        }
    });

    wireUpRightClick();
});
